import React, {useState} from 'react';
import {useNavigate} from 'react-router-dom';
import * as auth from '../auth.js';

const Login = (props) => {
    const navigate = useNavigate();
    const [formValue, setFormValue] = useState({
        email: '',
        password: ''
    })

    const tokenCheck = () => {
        const jwt = localStorage.getItem('jwt');

        if (jwt) {
            mestoAuth.getContent(jwt)
                .then(user => {
                    const url = location.state?.backUrl || '/content';
                    handleLogin(user)
                    navigate(url)
                })
                .catch(err => console.log(err))
        }
    }

    useEffect(() => {
        tokenCheck()
    }, []);

    const handleChange = (e) => {
        const {name, value} = e.target;

        setFormValue({
            ...formValue,
            [name]: value
        });
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        if (!formValue.email || !formValue.password){
            return;
        }
        auth.authorize(formValue.email, formValue.password)
        .then((data) => {
            if (data.jwt){
                setFormValue({email: '', password: ''});
                props.handleLogin();
                navigate('/', {replace: true});
            }
        })
        .catch(err => console.log(err));
    }

    return (
        <div className="login">
            <p className="login__welcome">Вход</p>
            <form className="login__form">
                <input className="login__input" required id="username" name="username" type="text" value={formValue.username || ""} placeholder="Email" onChange={handleChange} />
                <input className="login__input" required id="password" name="password" type="password" value={formValue.password || ""} placeholder="Пароль" onChange={handleChange} />
                <button onSubmit={handleSubmit} type="submit" className="login__button">Войти</button>
            </form>
        </div>
    )
}

export default Login;

