import React, {useState} from 'react';
import {useNavigate} from 'react-router-dom';
import * as auth from '../auth.js';

const Login = (props) => {
    const navigate = useNavigate();
    const [formValue, setFormValue] = useState({
        username: '',
        password: ''
    })

    const handleChange = (e) => {
        const {name, value} = e.target;

        setFormValue({
            ...formValue,
            [name]: value
        });
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        if (!formValue.username || !formValue.password){
            return;
        }
        auth.authorize(formValue.username, formValue.password)
        .then((data) => {
            if (data.jwt){
                setFormValue({username: '', password: ''});
                props.handleLogin();
                navigate('/', {replace: true});
            }
        })
        .catch(err => console.log(err));
    }

    return (
        <div className="login">
            <p>Регистрация</p>
            <p className="login__welcome">Вход</p>
            <form onSubmit={handleSubmit} className="login__form">
                <input required id="username" name="username" type="text" value={formValue.username} placeholder="Email" onChange={handleChange} />
                <input required id="password" name="password" type="password" value={formValue.password} placeholder="Пароль" onChange={handleChange} />
                <div className="login__button-container">
                    <button type="submit" className="login__button">Войти</button>
                </div>
            </form>
        </div>
    )
}

export default Login;
