import React from 'react';

//export default 
function PopupWithForm(props) {

    const className = `popup popup_type_${props.name} ${props.isOpen && `popup_opened`}`;
    const formName = `form form_type_${props.name}`;
    const buttonName = `form__button-save form__button-save_type_${props.name}`;
    const titleName = `popup__title popup__title_type_${props.name}`;

    return (
        <div className={className} >
            <div className="popup__container popup__container_type_form">
                <h2 className={titleName}>{`${props.title}`}</h2>
                <form 
                action="submit" 
                className={formName} 
                name={`form-${props.name}`}>
                    {props.children}
                    <button type="submit" className={buttonName}>{props.text}</button>
                </form>
                <button className="popup__button-close" type="button" onClick={props.onClose}></button>
            </div>
        </div>
    );
}

export default PopupWithForm;
