import React from 'react';
import PopupWithForm from './PopupWithForm.js';
import CurrentUserContext from '../context/CurrentUserContext.js';

function EditAvatarPopup(props) {

    const currentUser = React.useContext(CurrentUserContext);
    const avatarRef = React.useRef();

    React.useEffect(() => {
        avatarRef.current.value = ""
  }, [props.isOpen]);

    function handleChange(e) {
        avatarRef(e.target.avatar);
    }

    function handleSubmit(e) {
        e.preventDefault();

        props.onUpdateAvatar({
          avatar: avatarRef.current.value /* Значение инпута, полученное с помощью рефа */
        });
      }

    return (
        <PopupWithForm onSubmit={handleSubmit} name="avatar" title="Обновить аватар" text="Сохранить" onClose={props.onClose} isOpen={props.isOpen} forms="avatar">
            <input ref={avatarRef} onChange={handleChange} className="form__input form__input_change_avatar" id="avatar" name="avatar" type="url" alt="" placeholder="Ссылка на аватар" value={avatar} autoComplete="off" required />
            <span className="form__error" id="avatar-error"></span>
        </PopupWithForm>
    )
}
export default EditAvatarPopup;
