import React from 'react';
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  //const [isImagePopupOpen, setIsImagePopupOpen] = React.useState(false);

  function handleAvatarEditClick() {
    setIsEditAvatarPopupOpen(true);  
  }
  function handleProfileEditClick() {
    setIsEditProfilePopupOpen(true)
  }
  function handlePlaceAddClick() {
    setIsAddPlacePopupOpen(true)
  }
  //function handleCardClick(card){
    //setSelectedCard(card)
    //setIsImagePopupOpen(true);
  //}

  return (
    <>
      <Header />
      <Main editAvatarClick={handleAvatarEditClick} editProfileClick={handleProfileEditClick} addPlaceClick={handlePlaceAddClick}/>
      <Footer />
      <div className="popup popup_type_edit">
        <div className="popup__container popup__container_type_form">
          <h2 className="popup__title popup__title_type_form">Редактировать профиль</h2>
          <form className="form form_type_edit" name="form-edit" novalidate>
            <input className="form__input form__input_edit_name" minlength="2" maxlength="40" id="user-name" name="nickname" type="text" placeholder="Имя профиля" value="" autocomplete="off" required></input>
            <span className="form__error" id="user-name-error"></span>
            <input className="form__input form__input_edit_about" minlength="2" maxlength="200" id="user-about" name="about" type="text" placeholder="Описание профиля" value="" autocomplete="off" required></input>
            <span className="form__error" id="user-about-error"></span>
            <button className="form__button-save form__button-save_type_edit" type="submit">Сохранить</button>
          </form>
          <button className="popup__button-close" type="button"></button>
        </div>
      </div>

      <div className="popup popup_type_place">
        <div className="popup__container popup__container_type_form">
          <h2 className="popup__title popup__title_type_form">Новое место</h2>
          <form className="form form_type_place" name="form-place" novalidate>
            <input className="form__input form__input_add_name" minlength="2" maxlength="30" id="place-name" name="placeName" type="text" placeholder="Название" value="" autocomplete="off" required></input>
            <span className="form__error" id="place-name-error"></span>
            <input className="form__input form__input_add_link" id="place-link" name="placeLink" type="url" alt="" placeholder="Ссылка на картинку" value="" autocomplete="off" required></input>
            <span className="form__error" id="place-link-error"></span>
          </form>
          <button className="popup__button-close" type="button"></button>
        </div>
      </div>

      <div className="popup popup_type_image">
        <figure className="popup__container popup__container_type_image">
          <button className="popup__button-close" type="button"></button>
          <img className="popup__picture" src="#" alt=""/>
          <figcaption className="popup__title popup__title_type_image"></figcaption>
        </figure>
      </div>

      <div className="popup popup_type_agreement">
        <div className="popup__container popup__container_type_agreement">
          <h2 className="popup__title popup__title_type_form">Вы уверены?</h2>
          <form className="form form_type_agreement" name="form-agreement" novalidate>
            <button className="form__button-save form__button-save_type_agreement" type="submit">Да</button>
          </form>
          <button className="popup__button-close" type="button"></button>
        </div>
      </div>

      <div className="popup popup_type_avatar">
        <div className="popup__container popup__container_type_avatar">
          <h2 className="popup__title popup__title_type_form">Обновить аватар</h2>
          <form className="form form_type_avatar" name="form-avatar" novalidate>
            <input className="form__input form__input_change_avatar" id="avatar" name="avatar" type="url" alt="" placeholder="Ссылка на аватар" value="" autocomplete="off" required></input>
            <span className="form__error" id="avatar-error"></span>
            <button className="form__button-save form__button-save_type_avatar" type="submit">Сохранить</button>
          </form>
          <button className="popup__button-close" type="button"></button>
        </div>
      </div>

      <template className="card-template">
        <article className="photo-card">
          <img className="photo-card__picture photo-card__button-picture" src="#" alt=" "/>
          <div className="photo-card__group">
            <h2 className="photo-card__title"></h2>
            <div className="like">
              <button className="like__button" type="button"></button>
              <h3 className="like__number">6</h3>
            </div>
          </div>
          <button className="photo-card__button-delete" type="button"></button>
        </article>
      </template>
    </>
  );
}

export default App;
