import React, {useState} from 'react';
import {Link, useNavigate} from 'react-router-dom';
import * as auth from '../auth.js';

const Register = () => {
  const [formValue, setFormValue] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    calGoal: ''
  })
  const navigate = useNavigate();

  const handleChange = (e) => {
    const {name, value} = e.target;

    setFormValue({
      ...formValue,
      [name]: value
    });
  }
  const handleSubmit = (e) => {
    e.preventDefault();
    if (formValue.password === formValue.confirmPassword){
      auth.register(formValue.username, formValue.password, formValue.email).then((res) => {
        navigate('/login', {replace: true});
        }
      );
    }
  }

  return (
    <div className="login">
        <p className="login__welcome">Регистрация</p>
        <form onSubmit={handleSubmit} className="login__form">
            <input className="login__input" required id="username" name="username" type="text" value={formValue.username} placeholder="Email" onChange={handleChange} />
            <input className="login__input" required id="password" name="password" type="password" value={formValue.password} placeholder="Пароль" onChange={handleChange} />
            <button type="submit" className="login__button">Зарегистрироваться</button>
        </form>
        <div className="login__signin">
            <p className="login__text">Уже зарегистрированы?</p>
            <Link to="login" className="login__link"> Войти</Link>
        </div>
    </div>
)
}

export default Register;
